        public void InsertStatesIntoDbAccesToday(int StateDuration, int CncId, string CncName)
        {
        	ClearStatesTablesTableContents(CncId);
        	ClearTopsTableContents(CncId);
        	System.DateTime MaDateNow=System.DateTime.Now;
        	System.DateTime MaDateYesterday = System.DateTime.Today.AddDays(-1);
        	System.DateTime MaDateStartLoop = MaDateYesterday;
  			System.DateTime MaDateLast = MaDateYesterday;
			// Creation des état du jours precedant tout les un quart d'heure  			
        	for (int i = 0; i < 23; i++) {
	    		InsertTopInDb(CncId, 0, MaDateStartLoop.AddMinutes(60*i), MaDateStartLoop.AddMinutes(15).AddMinutes(60*i) );
	         	InsertTopInDb(CncId, 1, MaDateStartLoop.AddMinutes(15).AddMinutes(60*i), MaDateStartLoop.AddMinutes(30).AddMinutes(60*i));
	        	InsertTopInDb(CncId, 2, MaDateStartLoop.AddMinutes(30).AddMinutes(60*i), MaDateStartLoop.AddMinutes(45).AddMinutes(60*i));
	        	InsertTopInDb(CncId, 3, MaDateStartLoop.AddMinutes(45).AddMinutes(60*i), MaDateStartLoop.AddMinutes(60).AddMinutes(60*i));
				MaDateLast = MaDateStartLoop.AddMinutes(60).AddMinutes(60*i);	        	
        	}
			// création des deux derniers état
			// l état courant dure depuis une heure
			// l état precedent commence hier 23h.			
//  			InsertTopInDb(1, 4, MaDateLast, MaDateNow.AddMinutes(-60) );
//  			InsertTopInDb(1, 0, MaDateNow.AddMinutes(-60), MaDateNow.AddMinutes(-60) );
//  			UpdateCworkstatestable(1, 0, MaDateNow.AddMinutes(-60),"cnc_1","Production");
			// creation du dernier etat d'hiere
			InsertTopInDb(CncId, 4, MaDateLast, System.DateTime.Today );
//  			InsertTopInDb(CncId, 0, System.DateTime.Today, System.DateTime.Today );
//  			UpdateCworkstatestable(1, 0, System.DateTime.Today,CncName,"Production");

			// Creation des etats d'aujourd'hui
			
			//calcul du nombre de boucle de creation de 4 états
			int nbSecondToNow = Convert.ToInt32((MaDateNow-System.DateTime.Today).TotalSeconds);
			int cycleDuration= StateDuration*4;
			int TotalCycle= nbSecondToNow / cycleDuration;
			int CountTopInComment=0;
			MaDateStartLoop = System.DateTime.Today;
  			MaDateLast = System.DateTime.Today;
			
			for (int i = 0; i < TotalCycle-1 ; i++) {
  				InsertTopInDb(CncId, 0, MaDateStartLoop.AddSeconds(cycleDuration*i), MaDateStartLoop.AddSeconds(StateDuration).AddSeconds(cycleDuration*i), "top"+CountTopInComment.ToString() );
				CountTopInComment = CountTopInComment+1;
  				InsertTopInDb(CncId, 1, MaDateStartLoop.AddSeconds(StateDuration).AddSeconds(cycleDuration*i), MaDateStartLoop.AddSeconds(2*StateDuration).AddSeconds(cycleDuration*i), "top"+CountTopInComment.ToString());
				CountTopInComment = CountTopInComment+1;
	        	InsertTopInDb(CncId, 2, MaDateStartLoop.AddSeconds(2*StateDuration).AddSeconds(cycleDuration*i), MaDateStartLoop.AddSeconds(3*StateDuration).AddSeconds(cycleDuration*i), "top"+CountTopInComment.ToString());
				CountTopInComment = CountTopInComment+1;
	        	InsertTopInDb(CncId, 3, MaDateStartLoop.AddSeconds(3*StateDuration).AddSeconds(cycleDuration*i), MaDateStartLoop.AddSeconds(4*StateDuration).AddSeconds(cycleDuration*i), "top"+CountTopInComment.ToString());
				CountTopInComment = CountTopInComment+1;
				MaDateLast = MaDateStartLoop.AddSeconds(4*StateDuration).AddSeconds(cycleDuration*i);					
			}
  			// etat courant
			InsertTopInDb(CncId, 0, MaDateLast, MaDateLast );
			UpdateCworkstatestable(CncId, 0, MaDateLast,CncName,"Production");

        }   

